CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_visita_tecnica ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nr_seq_tipo_visita_p pls_visita_tecnica.nr_seq_tipo_visita%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

cd_estabelecimento_w	pls_creden_prestador.cd_estabelecimento%type;
nr_seq_prestador_w	pls_creden_prestador.nr_seq_prestador%type;
nr_seq_visita_w		pls_visita_tecnica.nr_sequencia%type;

BEGIN

begin
select	cd_estabelecimento,
	nr_seq_prestador
into STRICT	cd_estabelecimento_w,
	nr_seq_prestador_w
from	pls_creden_prestador
where	nr_sequencia	= nr_seq_credenciamento_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

insert	into	pls_visita_tecnica(	nr_sequencia, nr_seq_tipo_visita, cd_estabelecimento,
		ie_status, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_credenciamento,
		nr_seq_prestador )
	values (	nextval('pls_visita_tecnica_seq'), nr_seq_tipo_visita_p, cd_estabelecimento_w,
		0, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, nr_seq_credenciamento_p,
		nr_seq_prestador_w )
	returning nr_sequencia into nr_seq_visita_w;

CALL pls_visita_tecnica_prest_pck.liberar_visita(nr_seq_visita_w,nm_usuario_p,'S');

update	pls_creden_prestador
set	ie_status		= '8',
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_credenciamento_p;

END;

$body$
