SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_creden_prestador_pck.obter_especialidades ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);

C01 CURSOR FOR
	SELECT	distinct e.cd_especialidade,
		e.ds_especialidade
	from	pls_cred_prest_med_espec p,
		especialidade_medica e
	where	e.cd_especialidade = p.cd_especialidade
	and	p.nr_seq_credenciamento = nr_seq_credenciamento_p
	order by e.ds_especialidade desc;
BEGIN
for c01_w in c01 loop
	begin
	ds_retorno_w	:= ds_retorno_w || ', ' || c01_w.ds_especialidade;
	end;
end loop;

if (substr(ds_retorno_w, 1, 2) = ', ') then
	ds_retorno_w	:= substr(ds_retorno_w, 2, length(ds_retorno_w));
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
