CREATE OR REPLACE FUNCTION pls_creden_prestador_pck.obter_se_cadastro_editavel ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, ie_tipo_informacao_p pls_cred_prest_revisao.ie_tipo_informacao%type) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w	varchar(1) := 'N';
ie_status_w	pls_creden_prestador.ie_status%type;
nr_registro_w	integer;


BEGIN

begin
	select	ie_status
	into STRICT	ie_status_w
	from	pls_creden_prestador
	where	nr_sequencia	= nr_seq_credenciamento_p;
exception
when others then
	ie_status_w	:= null;
end;

if (ie_status_w = 1) then
	ds_retorno_w	:= 'S';
elsif (ie_status_w = 3) then
	select	coalesce(max(1), -1)
	into STRICT	nr_registro_w
	from	pls_cred_prest_revisao r
	where	r.nr_seq_credenciamento = nr_seq_credenciamento_p
	and	r.ie_tipo_informacao	= ie_tipo_informacao_p
	and	coalesce(r.dt_liberacao::text, '') = '';
	
	if (nr_registro_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
