CREATE OR REPLACE PROCEDURE pls_cta_alt_dados_pck.pls_gerencia_alt_dados ( cd_acao_analise_p pls_acao_analise.cd_acao%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_conta_principal_p text, ie_atualiza_seg_ref_p text, ie_atual_seg_outras_analises_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_grupo_p pls_auditoria_conta_grupo.nr_seq_grupo%type) AS $body$
BEGIN
case cd_acao_analise_p
	/*Modificar informações da conta*/

	when 6	then
		pls_atualizar_conta_analise( nr_seq_analise_p,nr_seq_conta_p,'A', ie_conta_principal_p, ie_atualiza_seg_ref_p, ie_atual_seg_outras_analises_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_grupo_p);
end case;

update	pls_conta
set	ie_consistir_conta_analise = 'S'
where	nr_seq_analise	= nr_seq_analise_p;

CALL pls_gerencia_dados_ocor_pck.pls_reconsistir_ocor( nr_seq_conta_p, null,  null,
						  null,cd_acao_analise_p,cd_estabelecimento_p,
						  nm_usuario_p);

commit;

END;

$body$
