SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_cta_alt_valor_pck.pls_aceitar_vl_calc_mat ( nr_seq_conta_p pls_conta_v.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_v.nr_sequencia%type, cd_acao_analise_p pls_acao_analise.cd_acao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_tipo_conta_w   pls_conta_v.ie_tipo_conta%type;
vl_unit_lib_w     pls_conta_mat_v.vl_unitario%type;
vl_glosa_w     pls_conta_mat_v.vl_glosa%type;
vl_glosa_taxa_w   pls_conta_mat_v.vl_glosa_taxa_material%type;

C01 CURSOR(nr_seq_conta_mat_p    pls_conta_mat_v.nr_sequencia%type) FOR
  SELECT  a.nr_sequencia,
    a.vl_material_imp,
    a.qt_material_imp,
    a.vl_taxa_material_imp,
    obter_valor_menor(a.vl_material, a.vl_material_imp) vl_material_lib,
    obter_valor_menor(a.vl_taxa_material, a.vl_taxa_material_imp) vl_taxa_material_lib,
    a.vl_material
  from  pls_conta_mat_v a
  where  a.nr_sequencia  = nr_seq_conta_mat_p;

BEGIN
-- obt?m o tipo de conta
ie_tipo_conta_w := pls_util_cta_pck.pls_obter_tipo_conta(nr_seq_conta_p);

for r_C01_w in C01(nr_seq_conta_mat_p) loop

  vl_unit_lib_w := round((dividir_sem_round(r_C01_w.vl_material_lib, r_C01_w.qt_material_imp))::numeric, 2);
  vl_glosa_w := pls_util_cta_pck.pls_obter_vl_glosa(r_C01_w.vl_material_lib, r_C01_w.vl_material_imp);

  -- se for interc?mbio
  if (ie_tipo_conta_w = 'I') then

    vl_glosa_taxa_w := pls_util_cta_pck.pls_obter_vl_glosa(r_C01_w.vl_taxa_material_lib, r_C01_w.vl_taxa_material_imp);

    -- Somar o valor de glosa da taxa ao valor de glosa total do item.
    -- OS 606930.
    -- atualizar pagamento e cobran?a (OPS - Processos de contas m?dicas)
    if (cd_acao_analise_p = 7) then
      update   pls_conta_mat set
        vl_liberado     = r_C01_w.vl_material_lib,
        qt_material    = r_C01_w.qt_material_imp,
        vl_unitario    = vl_unit_lib_w,
        vl_glosa    = vl_glosa_w,
        vl_lib_taxa_material  = r_C01_w.vl_taxa_material_lib,
        vl_glosa_taxa_material  = vl_glosa_taxa_w,
        nm_usuario    = nm_usuario_p,
        dt_atualizacao    = clock_timestamp()
      where  nr_sequencia    = r_C01_w.nr_sequencia;
    else
      -- processo padr?o
      update   pls_conta_mat set
        vl_liberado     = r_C01_w.vl_material_lib,
        qt_material    = r_C01_w.qt_material_imp,
        vl_unitario    = vl_unit_lib_w,
        vl_glosa    = vl_glosa_w,
        vl_lib_taxa_material  = r_C01_w.vl_taxa_material_lib,
        vl_glosa_taxa_material  = vl_glosa_taxa_w,
        ie_status_pagamento  = pls_obter_stat_pag_item(ie_glosa, r_C01_w.vl_material_lib, vl_glosa_w),
        nm_usuario    = nm_usuario_p,
        dt_atualizacao    = clock_timestamp(),
        ie_tipo_liberacao  = 'P',
        ie_status    = 'L'
      where  nr_sequencia    = r_C01_w.nr_sequencia;
    end if;

  else
    -- atualizar pagamento e cobran?a (OPS - Processos de contas m?dicas)
    if (cd_acao_analise_p = 7) then

      update   pls_conta_mat set
        vl_liberado     = r_C01_w.vl_material,
        qt_material    = r_C01_w.qt_material_imp,
        vl_unitario    = vl_unit_lib_w,
        vl_glosa    = vl_glosa_w,
        nm_usuario    = nm_usuario_p,
        dt_atualizacao    = clock_timestamp(),
        -- campos abaixo s?o zerados por seguran?a
        vl_lib_taxa_material  = 0,
        vl_glosa_taxa_material  = 0
      where  nr_sequencia    = r_C01_w.nr_sequencia;
    else
      update   pls_conta_mat set
        vl_liberado     = r_C01_w.vl_material,
        qt_material    = r_C01_w.qt_material_imp,
        vl_unitario    = vl_unit_lib_w,
        vl_glosa    = vl_glosa_w,
        ie_status_pagamento  = pls_obter_stat_pag_item(ie_glosa, r_C01_w.vl_material, vl_glosa_w),
        nm_usuario    = nm_usuario_p,
        dt_atualizacao    = clock_timestamp(),
        -- campos abaixo s?o zerados por seguran?a
        vl_lib_taxa_material  = 0,
        ie_tipo_liberacao  = 'P',
        vl_glosa_taxa_material  = 0,
        ie_status    = 'L'
      where  nr_sequencia    = r_C01_w.nr_sequencia;
    end if;
  end if;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cta_alt_valor_pck.pls_aceitar_vl_calc_mat ( nr_seq_conta_p pls_conta_v.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_v.nr_sequencia%type, cd_acao_analise_p pls_acao_analise.cd_acao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
