CREATE OR REPLACE PROCEDURE pls_cta_alt_valor_pck.pls_ajustar_valor_tx_mat ( nr_seq_conta_p pls_conta_v.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_v.nr_sequencia%type, qt_liberada_p pls_conta_proc_v.qt_procedimento%type, vl_liberado_p pls_conta_proc_v.vl_liberado%type, vl_lib_taxa_mat_p pls_conta_proc_v.vl_lib_taxa_material%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

--Rotina respons?vel por ajustar a taxa de interc?mbio liberada do material
vl_glosa_w     pls_conta_mat_v.vl_glosa%type;
vl_glosa_taxa_w   pls_conta_mat_v.vl_glosa_taxa_material%type;
vl_dif_taxa_material_w  pls_conta_mat_v.vl_lib_taxa_material%type;
vl_liberado_w    pls_conta_mat_v.vl_liberado%type;
vl_unitario_w    pls_conta_mat_v.vl_unitario%type;

C01 CURSOR(nr_seq_conta_mat_p    pls_conta_mat_v.nr_sequencia%type) FOR
  SELECT  a.nr_sequencia,
    a.vl_material_imp,
    a.vl_taxa_material_imp,
    a.vl_lib_taxa_material
  from  pls_conta_mat_v a
  where  a.nr_sequencia  = nr_seq_conta_mat_p;

BEGIN

for r_C01_w in C01(nr_seq_conta_mat_p) loop
  --nos casos de material como n?o existe o valor somente de material sem a taxa ? necess?rio subtrair a taxa liberada na funcionalidade da taxa anteiormente liberada para obter o valor liberado
  vl_dif_taxa_material_w  := r_C01_w.vl_lib_taxa_material - vl_lib_taxa_mat_p;

  vl_liberado_w  := vl_liberado_p - (vl_dif_taxa_material_w);

  vl_unitario_w  := dividir(vl_liberado_w,qt_liberada_p);
  vl_glosa_w := pls_util_cta_pck.pls_obter_vl_glosa(vl_liberado_w, r_C01_w.vl_material_imp);

  vl_glosa_taxa_w := pls_util_cta_pck.pls_obter_vl_glosa(vl_lib_taxa_mat_p, r_C01_w.vl_taxa_material_imp);

  -- Somar o valor de glosa da taxa ao valor de glosa total do item.
  -- OS 606930.
  update   pls_conta_mat set
    vl_liberado     = vl_liberado_w,
    qt_material    = qt_liberada_p,
    vl_unitario    = vl_unitario_w,
    vl_glosa    = vl_glosa_w,
    vl_lib_taxa_material  = vl_lib_taxa_mat_p,
    vl_glosa_taxa_material  = vl_glosa_taxa_w,
    ie_status_pagamento  = pls_obter_stat_pag_item(ie_glosa, vl_liberado_w, vl_glosa_w),
    nm_usuario    = nm_usuario_p,
    dt_atualizacao    = clock_timestamp(),
    ie_status    = 'L'
  where  nr_sequencia    = r_C01_w.nr_sequencia;

end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
