CREATE OR REPLACE PROCEDURE pls_cta_alt_valor_pck.pls_gerencia_ajustar_valor ( nr_seq_conta_p pls_conta_v.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc_v.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_v.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, qt_liberada_p pls_conta_proc_v.qt_procedimento%type, vl_liberado_p pls_conta_proc_v.vl_liberado%type, vl_unitario_p pls_conta_proc_v.vl_unitario%type, vl_liberado_hi_p pls_conta_proc_v.vl_liberado_hi%type, vl_liberado_mat_p pls_conta_proc_v.vl_liberado_material%type, vl_liberado_co_p pls_conta_proc_v.vl_liberado_co%type, vl_lib_taxa_hi_p pls_conta_proc_v.vl_lib_taxa_servico%type, vl_lib_taxa_mat_p pls_conta_proc_v.vl_lib_taxa_material%type, vl_lib_taxa_co_p pls_conta_proc_v.vl_taxa_co%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_glosar_pagamento_ocor_w      pls_ocorrencia.ie_glosar_pagamento%type;
ie_tipo_item_w      varchar(1);

BEGIN

select  max(coalesce(a.ie_glosar_pagamento,'S'))
into STRICT  ie_glosar_pagamento_ocor_w
from  pls_ocorrencia a
where  a.nr_sequencia  = nr_seq_ocorrencia_p;

-- somente pode alterar valores se o campo for S. Independente disto, a glosa pode ser lan?ada, ou seja, pode-se ter o lan?amento da glosa (tratado em outra rotina), mas o valor n?o pode ter altera??o
-- se n?o tiver ocorr?ncia informada, passa direto
if (ie_glosar_pagamento_ocor_w  = 'S') or (coalesce(nr_seq_ocorrencia_p::text, '') = '') then

  ie_tipo_item_w  := pls_util_cta_pck.pls_obter_tipo_item_atend(nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_conta_mat_p, null);

  case ie_tipo_item_w
    -- conta
    when 'C' then
      -- n?o implementado
      wheb_mensagem_pck.exibir_mensagem_abort(252581);
    -- procedimento
    when 'P' then
      -- atualiza os dados na tabela
      pls_cta_alt_valor_pck.pls_ajustar_valor_proc(  nr_seq_conta_p, nr_seq_conta_proc_p, qt_liberada_p, vl_liberado_p, vl_unitario_p,
            vl_liberado_hi_p, vl_liberado_mat_p, vl_liberado_co_p, vl_lib_taxa_hi_p, vl_lib_taxa_mat_p,
            vl_lib_taxa_co_p, nm_usuario_p);
      -- atualiza a tabela pls_proc_participante com os novos valores
      CALL CALL pls_cta_alt_valor_pck.pls_atualiza_proc_participante(  nr_seq_conta_proc_p, nm_usuario_p);
      -- atualiza dados complementares dos procedimentos
      CALL CALL pls_cta_alt_valor_pck.pls_atualiza_dados_comp_proc(nr_seq_conta_proc_p, nm_usuario_p);
    -- material
    when 'M' then
      -- atualiza os dados na tabela
      pls_cta_alt_valor_pck.pls_ajustar_valor_mat(  nr_seq_conta_p, nr_seq_conta_mat_p, qt_liberada_p, vl_liberado_p, vl_unitario_p,
            vl_lib_taxa_mat_p, nm_usuario_p);
    -- participante
    when 'R' then
      -- n?o implementado
      wheb_mensagem_pck.exibir_mensagem_abort(252583);
  end case;
end if;

END;

$body$
