CREATE OR REPLACE PROCEDURE pls_cta_consist_carac_item_pck.limpar_itens_liberados ( nr_seq_conta_p pls_conta.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Limpar a tabela dos itens liberados para a conta.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações
-------------------------------------------------------------------------------------------------------------------
jjung OS 629123 - 08/11/2013 -  Criação da procedure.
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
tb_reg_lib_w	dbms_sql.number_table;
i		integer;

  rw_tb_lib_w RECORD;

BEGIN

-- Inicializar a lista que irá conter os registros a serem deletados.
tb_reg_lib_w := current_setting('pls_cta_consist_carac_item_pck.num_tb_vazia_w')::dbms_sql.number_table;

-- Zerar o índice.
i := 0;
-- Varrer os registros de liberação de itens da conta passada por parâmetro.
for	rw_tb_lib_w in (	SELECT	a.nr_sequencia
				from	pls_conta_proc_regra_lib a
				where	a.nr_seq_conta_proc in (select	x.nr_sequencia
								from	pls_conta_proc x
								where	x.nr_seq_conta = nr_seq_conta_p
								and	x.ie_status <> 'C')) loop
	-- Armazenar a sequencia atual.
	tb_reg_lib_w(i) :=	rw_tb_lib_w.nr_sequencia;

	i := i + 1;
end loop;

-- Deletar todos os registros em um único statement.
forall i in tb_reg_lib_w.first..tb_reg_lib_w.last
	delete	FROM pls_conta_proc_regra_lib
	where	nr_sequencia = tb_reg_lib_w(i);

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
