CREATE OR REPLACE PROCEDURE pls_cta_consist_carac_item_pck.processar_regra ( dados_regra_p dados_regra) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Processar a regra passada por parâmetro.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações
-------------------------------------------------------------------------------------------------------------------
jjung OS 629123 - 08/11/2013 -  Criação da procedure.
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

-- Só será processada alguma regra válida.
if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '') then

	-- Alimentar todos os procedimentos como sendo válidos para a regra.
	CALL pls_cta_consist_carac_item_pck.alimentar_liberacao(dados_regra_p);

	-- Este controle é feito para que não seja feito  um select em cima da view PLS_CONTA_PROC_V sem ter nenhum procedimento liberado para
	-- esta regra.
	if (dados_regra_p.qt_procedimento_lib > 0) then

		-- Busca os procedimentos e faz o vínculo com as regras.
		CALL pls_cta_consist_carac_item_pck.processar_procedimentos(dados_regra_p);
		commit;
	end if;
end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cta_consist_carac_item_pck.processar_regra ( dados_regra_p dados_regra) FROM PUBLIC;
