SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.alterar_status_analise_cta ( tb_analise_p dbms_sql.number_table, ie_status_p pls_analise_conta.ie_status%type, ds_rotina_p text, ie_analise_nova_p pls_parametros.ie_analise_cm_nova%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

--Atualiza todas as an_lises cujo a sequencia esteja entre as contidas na lista

if (tb_analise_p.count > 0) then
	forall i in tb_analise_p.first..tb_analise_p.last
		update	pls_analise_conta
		set	ie_status	= ie_status_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			nm_proc_status	= ds_rotina_p
		where	nr_sequencia 	= tb_analise_p(i);
	commit;

	--Se for liberar para fechamento e valor do par_metro ie_analise_cm_nova = 'S'

	if	(ie_status_p = 'L' AND ie_analise_nova_p = 'S') then
		forall i in tb_analise_p.first..tb_analise_p.last
			update	pls_auditoria_conta_grupo
			set	dt_liberacao 		= clock_timestamp(),
				dt_final_analise	= clock_timestamp(),
				nm_auditor_atual	= CASE WHEN nm_auditor_atual = NULL THEN  'Tasy'  ELSE nm_auditor_atual END ,
				dt_atualizacao		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	nr_seq_analise		= tb_analise_p(i)
			and	coalesce(dt_liberacao::text, '') = '';
		commit;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
