CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.atualiza_dados_resumo_conta ( tb_resumo_insert_p table_dados_resumo_conta, tb_resumo_update_p table_dados_resumo_conta, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN
-- faz os inserts	

if (tb_resumo_insert_p.nr_seq_conta.count > 0) then
		forall i in tb_resumo_insert_p.nr_seq_conta.first..tb_resumo_insert_p.nr_seq_conta.last
			insert into 	pls_resumo_conta(nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_conta,
					ie_tipo_despesa, vl_apresentado, qt_apresentado,
					vl_liberado_sistema, vl_liberado_usuario, vl_pendente,
					vl_saldo, vl_glosa, qt_ocorrencia_pend)
		
					values (nextval('pls_resumo_conta_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, tb_resumo_insert_p.nr_seq_conta(i),
						tb_resumo_insert_p.ie_tipo_despesa(i), tb_resumo_insert_p.vl_apresentado(i), tb_resumo_insert_p.qt_apresentado(i),
						tb_resumo_insert_p.vl_liberado_sistema(i), tb_resumo_insert_p.vl_liberado_usuario(i), tb_resumo_insert_p.vl_pendente(i),
						tb_resumo_insert_p.vl_saldo(i), tb_resumo_insert_p.vl_glosa(i), tb_resumo_insert_p.qt_ocorrencia_pend(i));
		commit;
end if;
-- faz os updates

if (tb_resumo_update_p.nr_seq_conta.count > 0) then
		forall i in tb_resumo_update_p.nr_seq_conta.first..tb_resumo_update_p.nr_seq_conta.last
			update	pls_resumo_conta
			set 	vl_apresentado		= tb_resumo_update_p.vl_apresentado(i),
				qt_apresentado		= tb_resumo_update_p.qt_apresentado(i),
				vl_liberado_sistema	= tb_resumo_update_p.vl_liberado_sistema(i),
				vl_liberado_usuario	= tb_resumo_update_p.vl_liberado_usuario(i),
				vl_pendente		= tb_resumo_update_p.vl_pendente(i),
				vl_saldo		= tb_resumo_update_p.vl_saldo(i),
				vl_glosa		= tb_resumo_update_p.vl_glosa(i),
				qt_ocorrencia_pend	= tb_resumo_update_p.qt_ocorrencia_pend(i)
			where 	nr_sequencia		= tb_resumo_update_p.nr_sequencia(i);
		commit;

end if;
	
END;

$body$
