--Atualiza o status da conta para o status passado por par_metro.



CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.atualiza_status_conta ( tb_conta_p dbms_sql.number_table, tb_status_conta_p dbms_sql.varchar2_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

if (tb_conta_p.count > 0) then
	forall i in tb_conta_p.first..tb_conta_p.last
	update	pls_conta
	set	ie_status	= tb_status_conta_p(i),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= tb_conta_p(i);
	
	commit;	
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
