CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.atualiza_vl_provisao ( nr_seq_conta_p pls_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

update	pls_conta_proc
set	vl_provisao		= CASE WHEN coalesce(vl_procedimento_imp,0)=0 THEN  vl_procedimento  ELSE vl_procedimento_imp END ,	
	ie_origem_provisao  	= CASE WHEN coalesce(vl_procedimento_imp,0)=0 THEN  'C'  ELSE 'A' END ,
	nm_usuario		= nm_usuario_p		
where	nr_seq_conta    	= nr_seq_conta_p
and	coalesce(nr_lote_contabil_prov,0) = 0; --acrescentado o nvl pois quando realiza a exclusao do lote contabil o nr_lote_contabil_prov recebe 0 e n_o null, fazendo com que o vl_provisao fique nulo caso reconsistido

update	pls_conta_mat
set	vl_provisao		= CASE WHEN coalesce(vl_material_imp,0)=0 THEN  vl_material  ELSE vl_material_imp END ,
	ie_origem_provisao	= CASE WHEN coalesce(vl_material_imp,0)=0 THEN  'C'  ELSE 'A' END ,
	nm_usuario		= nm_usuario_p	
where	nr_seq_conta   		= nr_seq_conta_p
and	coalesce(nr_lote_contabil_prov,0) = 0; --acrescentado o nvl pois quando realiza a exclusao do lote contabil o nr_lote_contabil_prov recebe 0 e n_o null, fazendo com que o vl_provisao fique nulo caso reconsistido

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
