SET client_encoding TO 'UTF8';


--Atualiza o status do protocolo e tamb_m status na pls_conta_medica_resumo



CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.atualizar_status_protocolo ( tb_protocolo_p dbms_sql.number_table, tb_status_p dbms_sql.varchar2_table, tb_resumo_p dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

if (tb_protocolo_p.count > 0) then

	forall i in tb_protocolo_p.first..tb_protocolo_p.last
		update	pls_protocolo_conta
		set	ie_status		= tb_status_p(i),
			dt_fechamento_contas	= CASE WHEN  tb_status_p(i)=5 THEN  clock_timestamp()  ELSE null END ,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= tb_protocolo_p(i)
		and	ie_status		<> '7';
	commit;
end if;

if (tb_resumo_p.count > 0) then
	
	forall i in tb_resumo_p.first..tb_resumo_p.last
		update	pls_conta_medica_resumo	
		set	ie_status_protocolo 	= '5',
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_protocolo	= tb_resumo_p(i)
		and	ie_situacao		= 'A';
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
