--Passa os materiais e procedimentos em an_lise para liberado pelo usu_rio



CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.libera_itens_em_analise ( tb_conta_p dbms_sql.number_table) AS $body$
DECLARE


qt_item_analise_w		integer;


BEGIN

if (tb_conta_p.count > 0) then
	
	--Procedimentos em an_lise, seta status para liberado pelo usu_rio

	forall i in tb_conta_p.first..tb_conta_p.last
		update	pls_conta_proc a
		set	ie_status 	= 'L'
		where	nr_seq_conta 	= tb_conta_p(i)
		and	ie_status <> 'D'
		and	exists (	SELECT	1
				from 	pls_conta_proc b
				where	b.nr_seq_conta = a.nr_seq_conta
				and	b.ie_status = 'A');
	commit;
	
	--Materiais em an_lise, seta status para liberado pelo usu_rio

	forall	i in tb_conta_p.first..tb_conta_p.last
		update	pls_conta_mat a
		set	ie_status 	= 'L'
		where	nr_seq_conta 	= tb_conta_p(i)
		and	ie_status <> 'D'
		and	exists (	SELECT	1
				from	pls_conta_mat b
				where	b.nr_seq_conta	= a.nr_seq_conta
				and	ie_status	= 'A');
	
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
