CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.liberar_automatic_analise ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

						
ie_status_w	pls_analise_conta.ie_status%type;
qt_auditor_w	bigint;
BEGIN
for r_C01_w in current_setting('pls_cta_consistir_pck.c_fechar_contas')::CURSOR(( nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p) loop
	if (r_C01_w.nr_seq_analise is  not null) then
		if (r_C01_w.ie_status_analise = 'G') then
			select	count(1)
			into STRICT	qt_auditor_w
			from	pls_auditoria_conta_grupo
			where	(dt_inicio_analise IS NOT NULL AND dt_inicio_analise::text <> '')
			and	nr_seq_analise = r_C01_w.nr_seq_analise;
			
			if (qt_auditor_w = 0) then
				CALL pls_gerar_lib_analise(r_C01_w.nr_seq_analise, cd_estabelecimento_p, nm_usuario_p );
			end if;	
		end if;
	end if;	
end loop;
END;

$body$
