SET client_encoding TO 'UTF8';


--Obt_mvalores de glosa e liberado n_o conferem com valor do item

--retorna sequ_ncia do item



CREATE OR REPLACE FUNCTION pls_cta_consistir_pck.obter_inconsist_val_lib_glosa (nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_proc_glosa_inv_w	pls_conta_proc.nr_sequencia%type := null;
vl_processado_w		bigint;

C01 CURSOR(nr_seq_conta_pc	pls_conta.nr_sequencia%type)FOR
	SELECT	nr_sequencia,
		vl_liberado,	
		vl_glosa,
		vl_procedimento,
		vl_procedimento_imp,
		ie_valor_base
	from	pls_conta_proc_v
	where	nr_seq_conta = nr_seq_conta_pc
	and	coalesce(ie_tipo_liberacao::text, '') = '';

BEGIN

-- Consist_ncia de  valores de glosa e liberados antes de fechar a conta 

--verifica se valor de glosa e liberado conferem com valor do item

for	r_C01_w in C01(nr_seq_conta_p) loop
	
	--Se nr_seq_proc_glosa_inv_w for diferente de nulo, quer dizer que j_ encontrou um item 

	--e n_o precisa mais verificar nada.

	if (nr_seq_proc_glosa_inv_w IS NOT NULL AND nr_seq_proc_glosa_inv_w::text <> '') then
	
		--essa soma _ feita para simplificar as compara__es 

		vl_processado_w := r_C01_w.vl_liberado + r_C01_w.vl_glosa;
		
		--Valor base para a regra de c_lculo do procedimento 

		if (r_C01_w.ie_valor_base = 1 and r_C01_w.vl_procedimento_imp <> 0) then
			
			--Se valor glosado + liberado n_o forem igual ao valor do item

			if (vl_processado_w <> r_C01_w.vl_procedimento_imp) then
				
				if	not(r_C01_w.vl_glosa = 0 AND r_C01_w.vl_liberado > r_C01_w.vl_procedimento_imp ) then
					nr_seq_proc_glosa_inv_w := r_C01_w.nr_sequencia;
				end if;
			end if;
		end if;
		
		if (r_C01_w.ie_valor_base in ('2','3') and r_C01_w.vl_procedimento_imp = 0) then
			
			--Soma do valor de glosa + liberado  direfente do valor do procedimento

			if (vl_processado_w <> r_C01_w.vl_procedimento) then
				
				nr_seq_proc_glosa_inv_w := r_C01_w.nr_sequencia;	
			end if;
		end if;
	end if;
	
end loop;

return nr_seq_proc_glosa_inv_w;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
