SET client_encoding TO 'UTF8';


--Retorna a quantidade de contas do protocolo que estiverem com um determinado status



CREATE OR REPLACE FUNCTION pls_cta_consistir_pck.obter_qtde_contas ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, ie_tipo_consulta_p text) RETURNS integer AS $body$
DECLARE

qt_itens_w	integer := 0;


BEGIN
--Retorna todas as contas do protocolo

if (ie_tipo_consulta_p = 'T') then
	select	count(1)
	into STRICT	qt_itens_w
	from	pls_conta
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
	
--Retorna as contas com status 'F' ou 'C'

elsif (ie_tipo_consulta_p = 'F') then
	select	count(1)
	into STRICT	qt_itens_w
	from	pls_conta
	where	nr_seq_protocolo	= nr_seq_protocolo_p
	and	ie_status		in ('F','C');
	
--Retorna contas com status entre 'P', 'L' e 'A'

elsif (ie_tipo_consulta_p = 'P') then
	select	count(1)
	into STRICT	qt_itens_w
	from	pls_conta
	where	nr_seq_protocolo	= nr_seq_protocolo_p
	and	ie_status		in ('P','L','A');
end if;

return qt_itens_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
