--Retorna quantidade de contas em uma determinada an_lise com o status passado por par_metro



CREATE OR REPLACE FUNCTION pls_cta_consistir_pck.qt_contas_na_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, ie_status_p pls_conta.ie_status%type) RETURNS integer AS $body$
DECLARE

--ie_status

--F - Fechada

--L - Liberada para fechamento							

qt_contas_w	integer;


BEGIN
	select	count(nr_sequencia)
		into STRICT	qt_contas_w
		from	pls_conta
		where	nr_seq_analise = nr_seq_analise_p
		and	ie_status <> ie_status_p;

return qt_contas_w;		

END;

$body$
