SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_cta_consistir_pck.verifica_glosa ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_origem_regra_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

				
ie_evento_w	varchar(10);

c_contas_glosa CURSOR(	nr_seq_lote_pc		pls_lote_protocolo_conta.nr_sequencia%type,
			nr_seq_protocolo_pc	pls_protocolo_conta.nr_sequencia%type,
			nr_seq_lote_processo_pc	pls_cta_lote_processo.nr_sequencia%type,
			nr_seq_conta_pc		pls_conta.nr_sequencia%type) FOR
-- feito com union por motivo de performance, antes era feito em um select s_ com OR e em bases grandes apresentava problemas de performance

-- n_o meche em ocorrencias de contas que est_o canceladas ou fechadas

SELECT	a.nr_sequencia nr_seq_conta,
	a.ie_tipo_conta
from	pls_conta_v a
where	a.nr_seq_lote_conta = nr_seq_lote_pc
and	a.ie_status <> 'C'
and	a.ie_status <> 'F'

union all

SELECT	a.nr_sequencia nr_seq_conta,
	a.ie_tipo_conta
from	pls_conta_v a
where	a.nr_seq_protocolo = nr_seq_protocolo_pc
and	a.ie_status <> 'C'
and	a.ie_status <> 'F'

union all

select	a.nr_sequencia nr_seq_conta,
	a.ie_tipo_conta
from	pls_conta_v a
where	exists (select	1
		from	pls_cta_lote_proc_conta p
		where	p.nr_seq_lote_processo = nr_seq_lote_processo_pc
		and	p.nr_seq_conta = a.nr_sequencia)
and	a.ie_status <> 'C'
and	a.ie_status <> 'F'

union all

select	a.nr_sequencia nr_seq_conta,
	a.ie_tipo_conta
from	pls_conta_v a
where	a.nr_sequencia = nr_seq_conta_pc
and	a.ie_status <> 'C'
and	a.ie_status <> 'F';

BEGIN

--#D_cioAqui -> futuramente a rotina pls_atualizar_agrup_analise deve ser inutilizada e no lugar desta chamar a pls_atualizar_agrup_proc desta pck						

-- trata da organiza__o e hierarquia dos itens na tela (pr_-requisito para algumas valida__es)

pls_analise_cta_pck.pls_atualizar_agrup_proc(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p,
						nm_usuario_p, cd_estabelecimento_p, 'C', 'N');
commit;

for c_contas_glosa_w in c_contas_glosa(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p) loop
	
	-- muda evento conforme tipo de conta

	if (c_contas_glosa_w.ie_tipo_conta = 'I') then
		ie_evento_w		:= 'I5';
	else
		ie_evento_w		:= 'CC';
	end if;
	CALL pls_cta_consistir_pck.pls_inativa_glosa(c_contas_glosa_w.nr_seq_conta);
	-- gera as glosas

	CALL pls_cta_gerar_glosa(	c_contas_glosa_w.nr_seq_conta, cd_estabelecimento_p, nm_usuario_p,
				'S', coalesce(ie_origem_regra_p, ie_evento_w));

end loop;
/* Atualizar os pareceres das ocorr_ncias que foram inativas automaticamente */


CALL pls_cta_consistir_pck.pls_grava_fluxo_ocor(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
			nr_seq_conta_p, nm_usuario_p);
commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cta_consistir_pck.verifica_glosa ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_origem_regra_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
