CREATE OR REPLACE PROCEDURE pls_cta_gera_hono_cirurgia_pck.seta_execucao_aberta ( nr_sequencia_p pls_execucao_cirurgica.nr_sequencia%type, ie_tipo_faturamento_p pls_conta.ie_tipo_faturamento%type, nr_seq_exec_cir_guia_p pls_exec_cirurgica_guia.nr_sequencia%type) AS $body$
BEGIN	

	update 	pls_exec_cirurgica_guia
	set	dt_importacao = clock_timestamp() -- campo aqui foi criado com esse nome, mas refere-se a abertura da conta e nao importacao
	where 	nr_sequencia = nr_seq_exec_cir_guia_p;
	
	-- caso seja encerramento administrativo, permitira uma nova abertura
	if ( ie_tipo_faturamento_p in ('T','F')) then
		update 	pls_execucao_cirurgica
		set 	dt_abertura_contas_hi = clock_timestamp(),
				ie_faturado = 'S'
		where 	nr_sequencia = nr_sequencia_p;
	end if;
end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
