SET client_encoding TO 'UTF8';



-- cancela o participante e suas respectivas glosas e ocorrencias



CREATE OR REPLACE PROCEDURE pls_cta_gera_hono_indiv_pck.cancela_partic_abert_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_proc_partic_p pls_proc_participante.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

-- Alterado o status do participante para evitar cobranca duplicada OS 451709

update	pls_proc_participante	partic
set	ie_status 		= 'C',
	vl_participante		= 0,
	vl_calculado		= 0,
	ie_gerada_cta_honorario = 'S'
where	nr_seq_conta_proc	= nr_seq_conta_proc_p
and	exists (SELECT	1
		from	pls_conta_proc proc
		where	proc.nr_seq_participante_hi = partic.nr_sequencia);

-- cancela todas as glosas e ocorrencias vinculadas ao participante que foi cancelado

CALL pls_gerencia_dados_ocor_pck.pls_gerencia_situ_glo_ocor( nr_seq_conta_p,
							nr_seq_conta_proc_p,
							null,
							nr_seq_proc_partic_p,
							'I',
							nm_usuario_p);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
