CREATE OR REPLACE PROCEDURE pls_cta_pagto_faturamento_pck.atualiza_dados_pos_estab ( dados_pos_estab_p table_dados_pos_estab, tb_contas_p dbms_sql.number_table, tb_total_benef_p dbms_sql.number_table, ie_tipo_item_p text) AS $body$
BEGIN

if (ie_tipo_item_p = 'P') then
	if (dados_pos_estab_p.nr_seq_item.count > 0) then

		forall i in dados_pos_estab_p.nr_seq_item.first..dados_pos_estab_p.nr_seq_item.last
			update	pls_conta_pos_estabelecido
			set	vl_beneficiario		= dados_pos_estab_p.vl_liberado(i),
				qt_item			= dados_pos_estab_p.qt_item(i)
			where	nr_seq_conta_proc	= dados_pos_estab_p.nr_seq_item(i);
				
		forall i in dados_pos_estab_p.nr_seq_item.first..dados_pos_estab_p.nr_seq_item.last
			update	pls_conta_proc
			set	vl_beneficiario	= dados_pos_estab_p.vl_liberado(i)
			where	nr_sequencia	= dados_pos_estab_p.nr_seq_item(i);
		commit;
	end if;
end if;
	
if (tb_contas_p.count > 0) then
	
	forall i in tb_contas_p.first..tb_contas_p.last
		update	pls_conta
		set	vl_total_beneficiario	= tb_total_benef_p(i)
		where	nr_sequencia		= tb_contas_p(i);
	commit;
end if;


END;

$body$
LANGUAGE PLPGSQL
;
