CREATE OR REPLACE PROCEDURE pls_cta_pagto_faturamento_pck.atualiza_valor_glosa ( tb_vl_glosa_p dbms_sql.number_table, tb_seq_resumo_p dbms_sql.number_table) AS $body$
BEGIN

if (tb_vl_glosa_p.count > 0) then
	
	forall i in tb_vl_glosa_p.first..tb_vl_glosa_p.last
		update	pls_conta_medica_resumo
		set	vl_glosa = tb_vl_glosa_p(i)
		where	nr_sequencia = tb_seq_resumo_p(i);
	commit;
end if;

END;

$body$
