CREATE OR REPLACE PROCEDURE pls_cta_pagto_faturamento_pck.delete_conta_medica_resumo ( tb_seq_contas_p dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

--Se a variável tabela conter dados.
if (tb_seq_contas_p.count > 0) then
	-- Inativar os dados que já contém registro de pagamento que não pode ser desfeitos. Estes itens não serão excluídos
	-- e será impossível excluir a conta. Uma conta que estiver com esta situação NÃO pode ser excluída e, se for o caso
	-- deve ser cancelada apenas.
	forall i in tb_seq_contas_p.first..tb_seq_contas_p.last
		update	pls_conta_medica_resumo	a
		set	ie_situacao	= 'I',
			nm_usuario	= nm_usuario_P,
			dt_atualizacao	= clock_timestamp()
		where	nr_seq_conta	= tb_seq_contas_p(i)
		and	coalesce(nr_seq_lote_pgto::text, '') = ''
		and	exists (	SELECT	1
					from	pls_pag_prest_item_resumo	x
					where	x.nr_seq_conta		= a.nr_seq_conta
					and	x.nr_seq_resumo_conta 	= a.nr_sequencia);

	--Os registros correspondetes as contas que não forem inativados 
	forall i in tb_seq_contas_p.first..tb_seq_contas_p.last
		delete	FROM pls_conta_medica_resumo
		where	nr_seq_conta	= tb_seq_contas_p(i)
		and	((ie_situacao	<> 'I') or (coalesce(ie_situacao::text, '') = ''));
		
	commit;
end if;
	
END;

$body$
LANGUAGE PLPGSQL
;
