CREATE OR REPLACE FUNCTION pls_cta_pagto_faturamento_pck.obter_qt_pos_estab ( nr_seq_item_p pls_conta_proc.nr_sequencia%type, ie_tipo_item_p text) RETURNS integer AS $body$
DECLARE


qt_pos_estabelecido_w integer;
				

BEGIN
--P - Procedimentos
if (ie_tipo_item_p = 'P') then
	select	count(1)
	into STRICT	qt_pos_estabelecido_w
	from	pls_conta_pos_estabelecido
	where	nr_seq_conta_proc	= nr_seq_item_p;
--M - materiais
else
	select	count(1)
	into STRICT	qt_pos_estabelecido_w
	from	pls_conta_pos_estabelecido
	where	nr_seq_conta_mat	= nr_seq_item_p;
end if;

return qt_pos_estabelecido_w;

END;

$body$
