SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_cta_pagto_faturamento_pck.zera_valores_coparticipacao ( tb_seq_proc_p dbms_sql.number_table, tb_seq_contas_p dbms_sql.number_table, ie_tipo_item_p text) AS $body$
DECLARE

					
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nm_usuario_w		usuario.nm_usuario%type;
BEGIN

nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
--Na pls_conta_proc, existe o campo vl_coparticipacao, então o mesmo é zerado nesse momento,
--esse mesmo campo não existe na pls_conta_mat.
if ( ie_tipo_item_p = 'P') then
	if (tb_seq_proc_p.count > 0) then
		
		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			delete	FROM pls_conta_proc_aprop
			where	nr_seq_conta_proc = tb_seq_proc_p(i);
			
		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			delete	FROM pls_conta_copartic_aprop
			where	NR_SEQ_CONTA_COPARTICIPACAO in (SELECT	nr_sequencia
								from	pls_conta_coparticipacao
								where	nr_seq_conta_proc = tb_seq_proc_p(i));
		
		for i in tb_seq_proc_p.first..tb_seq_proc_p.last loop
			begin
				CALL pls_deletar_coparticipacao(tb_seq_contas_p(i),null,'S', 'N', null, null, nm_usuario_w,cd_estabelecimento_w);
			end;
		end loop;

		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			update	pls_conta_proc
			set	vl_coparticipacao = 0
			where 	nr_sequencia = tb_seq_proc_p(i);
		commit;
	end if;
--Materiais
else
	if (tb_seq_proc_p.count > 0) then
		
		
		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			delete	FROM pls_conta_mat_aprop
			where	nr_seq_conta_mat = tb_seq_proc_p(i);
			
		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			delete	FROM pls_conta_copartic_aprop
			where	NR_SEQ_CONTA_COPARTICIPACAO in (SELECT	nr_sequencia
								from	pls_conta_coparticipacao
								where	nr_seq_conta_mat = tb_seq_proc_p(i));	
		
		forall i in tb_seq_proc_p.first..tb_seq_proc_p.last
			delete	FROM pls_conta_coparticipacao
			where	nr_seq_conta_mat = tb_seq_proc_p(i);

	end if;

end if;

if (tb_seq_contas_p.count > 0) then
	forall i in tb_seq_contas_p.first..tb_seq_contas_p.last
		update 	pls_conta
		set	vl_coparticipacao = 0
		where 	nr_sequencia = tb_seq_contas_p(i);
	commit;
end if;	
	
END;

$body$
LANGUAGE PLPGSQL
;
