CREATE OR REPLACE PROCEDURE pls_cta_proc_mat_regra_pck.cria_registro_regra_proc_prin ( tb_nr_seq_conta_proc_princ_p INOUT dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Cria os registros de regra, com base no procedimento principal vinculado ao procedimento que
		se deseja criar a regra
		
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao: 

Alteracoes:

------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


tb_nr_seq_proc_princ_w	dbms_sql.number_table;


BEGIN

-- "compacta" os procedimentos principais, deixando apenas os que nao sao repetidos, para evitar insert desnecessario no forall

tb_nr_seq_proc_princ_w := pls_cta_proc_mat_regra_pck.distinct_number_table(tb_nr_seq_conta_proc_princ_p, tb_nr_seq_proc_princ_w);

forall i in tb_nr_seq_proc_princ_w.first..tb_nr_seq_proc_princ_w.last
	insert into pls_conta_proc_regra(nr_sequencia, dt_atualizacao, nm_usuario)
	SELECT	a.nr_sequencia,
		clock_timestamp(),
		nm_usuario_p
	from	pls_conta_proc		a
	where	a.nr_seq_proc_princ	= tb_nr_seq_proc_princ_w(i)
	and	not exists (	SELECT	1
				from	pls_conta_proc_regra	x
				where	x.nr_sequencia		= a.nr_sequencia);

END;

$body$
