CREATE OR REPLACE PROCEDURE pls_cta_proc_mat_regra_pck.cria_registro_regra_proc_tb ( tb_nr_seq_conta_proc_p INOUT dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_nr_seq_conta_proc_w	dbms_sql.number_table;


j			integer;
qt_registro_w		integer;

-- carrega os registros do vetor que ainda nao existem na tabela

/* COMPATIBILIDADE 10G 
cursor	c01(	tb_nr_seq_conta_proc_pc	dbms_sql.number_table) is
	select	distinct a.column_value,
		(select count(1) from pls_conta_proc_regra x where x.nr_sequencia = a.column_value) qt_existente
	from	table(tb_nr_seq_conta_proc_pc)	a;
*/

BEGIN

j := 0;
tb_nr_seq_conta_proc_w.delete;

for i in tb_nr_seq_conta_proc_p.first..tb_nr_seq_conta_proc_p.last loop

	-- feito a verificacao externa por conta da compatibilidade com o oracle 10g

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_conta_proc_regra	a
	where	a.nr_sequencia		= tb_nr_seq_conta_proc_p(i);
	
	if (qt_registro_w = 0) then
	
		tb_nr_seq_conta_proc_w(j) := tb_nr_seq_conta_proc_p(i);
		j := j + 1;
	end if;
end loop;

if (tb_nr_seq_conta_proc_w.count > 0) then

	tb_nr_seq_conta_proc_w := pls_cta_proc_mat_regra_pck.grava_registro_regra_proc(tb_nr_seq_conta_proc_w, nm_usuario_p);
end if;


END;

$body$
