CREATE OR REPLACE FUNCTION pls_cta_proc_mat_regra_pck.existe_valor_vetor ( tb_origem_p dbms_sql.number_table, valor_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	retorna se o valor passado existe dentro do vetor

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao: 

Alteracoes:

------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


ds_retorno_w	varchar(1);
BEGIN

ds_retorno_w := 'N';

-- para evitar erro de navegar por um vetor vazio

if (tb_origem_p.count > 0) then

	for i in tb_origem_p.first..tb_origem_p.last loop

		if (tb_origem_p(i) = valor_p) then
		
			ds_retorno_w := 'S';
			exit;
		end if;

	end loop;
end if;

return ds_retorno_w;

END;

$body$
