CREATE OR REPLACE FUNCTION pls_cta_proc_mat_regra_pck.get_max_seq_tiss_atend ( nr_sequencia_p pls_conta.nr_sequencia%type, nr_seq_segurado_p pls_conta.nr_seq_segurado%type) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna o max do sequencial tiss pertinente ao atendimento.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao: 

Alteracoes:

------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

ds_retorno_w	pls_conta_proc_regra.nr_seq_item_tiss%type;


BEGIN

select	max(t.nr_seq_item_tiss)
into STRICT	ds_retorno_w
from (	SELECT	max(a.nr_seq_item_tiss) nr_seq_item_tiss
	from	pls_conta_proc_regra	a,
		pls_conta_proc		b,
		pls_conta		c
	where	b.nr_sequencia		= a.nr_sequencia
	and	c.nr_sequencia		= b.nr_seq_conta
	and	c.nr_sequencia		= nr_sequencia_p
	and	c.nr_seq_segurado	= nr_seq_segurado_p
	
union all

	SELECT	max(a.nr_seq_item_tiss) nr_seq_item_tiss
	from	pls_conta_mat_regra	a,
		pls_conta_mat		b,
		pls_conta		c
	where	b.nr_sequencia		= a.nr_sequencia
	and	c.nr_sequencia		= b.nr_seq_conta
	and	c.nr_sequencia		= nr_sequencia_p
	and	c.nr_seq_segurado	= nr_seq_segurado_p) t;

return coalesce(ds_retorno_w, 0);

END;

$body$
