-- nos fontes é utilizado o código do processo (pls_cta_processo->cd_processo).
-- mas nos relacionamentos é preciso existir a sequencia da tabela e é por esse motivo que essa função existe
CREATE OR REPLACE FUNCTION pls_cta_processo_pck.pls_obter_seq_cd_processo ( cd_processo_p pls_cta_processo.cd_processo%type) RETURNS bigint AS $body$
DECLARE

nr_seq_processo_w	pls_cta_processo.nr_sequencia%type;

BEGIN
-- busca a sequencia
select	max(nr_sequencia)
into STRICT	nr_seq_processo_w
from	pls_cta_processo
where	cd_processo	= cd_processo_p;

return nr_seq_processo_w;

END;

