CREATE OR REPLACE PROCEDURE pls_cta_processo_pck.registra_processo_detalhe ( nr_seq_log_exec_p pls_cta_log_exec.nr_sequencia%type, cd_processo_p pls_cta_processo.cd_processo%type, ie_tipo_execucao_p text, nm_usuario_p usuario.nm_usuario%type, nr_seq_log_exec_desc_p INOUT pls_cta_log_exec_desc.nr_sequencia%type) AS $body$
DECLARE


nr_seq_processo_w	pls_cta_processo.nr_sequencia%type;


BEGIN
-- grava o registro e devolve o sequencial que foi criado
if (ie_tipo_execucao_p = 'INICIO_PROCESSO') then

	-- pega a sequencia que será usada
	select 	nextval('pls_cta_log_exec_desc_seq')
	into STRICT	nr_seq_log_exec_desc_p
	;

	nr_seq_processo_w := pls_cta_processo_pck.pls_obter_seq_cd_processo(cd_processo_p);

	insert	into pls_cta_log_exec_desc(
		nr_sequencia,
		dt_inicio,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_log_exec,
		nr_seq_processo
	) values (
		nr_seq_log_exec_desc_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_log_exec_p,
		nr_seq_processo_w);

else
	-- atualiza a data de fim de execução do processo
	update	pls_cta_log_exec_desc set
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_fim = clock_timestamp()
	where	nr_sequencia = nr_seq_log_exec_desc_p;
end if;

commit;

END;

