CREATE OR REPLACE PROCEDURE pls_cta_processo_pck.registra_processo ( nr_seq_lote_conta_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_proc_partic_p pls_proc_participante.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, cd_acao_analise_p pls_acao_analise.cd_acao%type, ds_lista_processo_p pls_cta_log_exec.ds_lista_processo%type, ds_param_extra_processo_p pls_cta_log_exec.ds_param_extra_processo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_tipo_execucao_p text, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_log_exec_p INOUT pls_cta_log_exec.nr_sequencia%type) AS $body$
BEGIN
-- grava o registro e devolve o sequencial que foi criado
if (ie_tipo_execucao_p = 'INICIO_PROCESSO') then

	-- pega a sequencia que será usada
	select 	nextval('pls_cta_log_exec_seq')
	into STRICT	nr_seq_log_exec_p
	;

	insert	into pls_cta_log_exec(
		nr_sequencia,
		cd_estabelecimento,
		dt_inicio,
		ds_lista_processo,
		ds_param_extra_processo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_protocolo,
		nr_seq_lote_processo,
		nr_seq_lote_conta,
		nr_seq_conta,
		nr_seq_conta_proc,
		nr_seq_proc_partic,
		nr_seq_conta_mat,
		nr_seq_acao,
		nr_seq_analise
	) values (
		nr_seq_log_exec_p,
		cd_estabelecimento_p,
		clock_timestamp(),
		ds_lista_processo_p,
		ds_param_extra_processo_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_protocolo_p,
		nr_seq_lote_processo_p,
		nr_seq_lote_conta_p,
		nr_seq_conta_p,
		nr_seq_conta_proc_p,
		nr_seq_proc_partic_p,
		nr_seq_conta_mat_p,
		pls_util_cta_pck.pls_obter_seq_acao_analise(cd_acao_analise_p),
		nr_seq_analise_p);

else
	-- atualiza a data de fim de execução do processo
	update	pls_cta_log_exec set
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_fim = clock_timestamp()
	where	nr_sequencia = nr_seq_log_exec_p;
end if;

commit;

END;

$body$
