CREATE OR REPLACE PROCEDURE pls_cta_valorizacao_pck.atualiza_conta_mat_cop ( tb_dados_conta_mat_p table_dados_conta_mat, tb_seq_conta_mat_insert_p dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

--Se realmente tiver registros

if (tb_seq_conta_mat_insert_p.count > 0) then

	--Atualiza referencia  a analise nas contas

	forall i in tb_seq_conta_mat_insert_p.first..tb_seq_conta_mat_insert_p.last
		update	pls_conta_mat
		set	vl_mat_copartic			= tb_dados_conta_mat_p.vl_mat_copartic(i),
			nr_seq_regra_copartic		= tb_dados_conta_mat_p.nr_seq_regra_copartic(i)
		where	nr_sequencia			= tb_seq_conta_mat_insert_p(i);		
	commit;
end if;
	
END;

$body$
