SET client_encoding TO 'UTF8';


--Atualiza o status dos itens de acordo com seu tipo(M - materiais, P- procedimentos).



CREATE OR REPLACE PROCEDURE pls_cta_valorizacao_pck.atualiza_status_item ( tb_seq_item_p dbms_sql.number_table, tb_status_pagamento_p dbms_sql.varchar2_table, ie_tipo_item_p text) AS $body$
BEGIN

--Verifica se ha registros nas listas


if (tb_seq_item_p.count > 0 ) then
	--Se estiver atualizando materiais

	if (ie_tipo_item_p = 'M') then
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	pls_conta_mat
			set	ie_status_pagamento	= tb_status_pagamento_p(i),
				ie_status		= CASE WHEN tb_status_pagamento_p(i)='I' THEN 'P'  ELSE ie_status END
			where	nr_sequencia		= tb_seq_item_p(i);
	--Se estiver atualizando procedimentos(tambem utilizado com os participantes)

	elsif (ie_tipo_item_p = 'P') then
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	pls_conta_proc
			set	ie_status_pagamento	= tb_status_pagamento_p(i),
				ie_status		= CASE WHEN tb_status_pagamento_p(i)='I' THEN 'P'  ELSE ie_status END
			where	nr_sequencia		= tb_seq_item_p(i);
	else --R para participante
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	pls_proc_participante
			set	ie_status_pagamento	= tb_status_pagamento_p(i)
			where	nr_sequencia		= tb_seq_item_p(i);
	end if;
	
	commit;
end if;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
