SET client_encoding TO 'UTF8';


-- Obter qual data deve ser utilizada para realizacao do calculo do prazo.



CREATE OR REPLACE FUNCTION pls_cta_valorizacao_pck.obter_data_item ( dados_conta_p dados_conta, dados_conta_proc_p dados_conta_proc, dados_conta_mat_p dados_conta_mat) RETURNS timestamp AS $body$
DECLARE

dt_item_w		timestamp;
cd_guia_referencia_w	pls_conta.cd_guia_referencia%type;

BEGIN


-- Quando a guia for de internacao entao deve se verificar se a conta ja tem data de alta, se tive entao esta data deve ser utilizada, caso contrario utiiliza apenas a data do adtendimento

if (dados_conta_p.ie_tipo_guia = 5) then

	-- Se a conta tiver data de alta utiliza esta data, caso contrario usar a do atendimento;

	if (dados_conta_p.dt_alta IS NOT NULL AND dados_conta_p.dt_alta::text <> '') then
		
		dt_item_w := dados_conta_p.dt_alta;
	else
		dt_item_w := dados_conta_p.dt_atendimento;
	end if;
else	
	--incluido o tratamento abaixo devido ao fato de quado possuir uma guia de resumo de internacao vinculada a data a ser utilizada e a data da internacao

	select	max(cd_guia_referencia)
	into STRICT	cd_guia_referencia_w
	from	pls_conta
	where	nr_sequencia	= dados_conta_p.nr_sequencia;
	
	select	max(dt_alta)
	into STRICT	dt_item_w
	from	pls_conta
	where	nr_seq_segurado	= dados_conta_p.nr_seq_segurado
	and	((cd_guia	= cd_guia_referencia_w) or (cd_guia_referencia	= cd_guia_referencia_w))
	and	ie_tipo_guia	= '5';
	
	if (coalesce(dt_item_w::text, '') = '') then
		-- Se nao for guia de Internacao entao utiliza apenas a data do atendimento.

		dt_item_w := dados_conta_p.dt_atendimento;
	end if;
end if;

return dt_item_w;

END;				

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
