SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_cta_valorizacao_pck.pls_gerencia_status_pagto_part ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE
	

ie_status_pagamento_w	varchar(1);	
ie_ocor_pagamento_w	varchar(1);	
tb_seq_item_w		dbms_sql.number_table;
tb_status_pagamento_w	dbms_sql.varchar2_table;
nr_sequencia_w		integer;
nr_indice_w		integer;
						
C01 CURSOR(	nr_seq_lote_pc			pls_lote_protocolo_conta.nr_sequencia%type,
		nr_seq_protocolo_pc		pls_protocolo_conta.nr_sequencia%type,
		nr_seq_lote_processo_pc		pls_cta_lote_processo.nr_sequencia%type,
		nr_seq_conta_pc			pls_conta.nr_sequencia%type,
		nr_seq_proc_pc			pls_conta_proc.nr_sequencia%type,
		nm_usuario_pc			usuario.nm_usuario%type,
		cd_estabelecimento_pc		estabelecimento.cd_estabelecimento%type) FOR	
		
	SELECT  part.ie_status,
		null qt_apresentada,
		part.qt_liberada,
		null vl_apresentado,
		part.vl_calculado,
		part.vl_participante,
		part.vl_glosa,
		part.ie_glosa,
		part.nr_sequencia
	From  	Pls_Conta_Proc_V Proc,
		pls_proc_participante  part
	Where  	Proc.Nr_Sequencia = Nr_Seq_Proc_Pc
	And  	Proc.Nr_Sequencia = part.nr_seq_conta_proc
	and	proc.ie_status 	in ('A', 'F', 'L', 'P', 'U')
	
union all

        SELECT  part.ie_status,
		null qt_apresentada,
		part.qt_liberada,
		null vl_apresentado,
		part.vl_calculado,
		part.vl_participante,
		part.vl_glosa,
		Part.Ie_Glosa,
		part.nr_sequencia
	From  	Pls_Conta_Proc_V Proc,
		pls_proc_participante  part
	Where  	Proc.Nr_Seq_Conta  = Nr_Seq_Conta_Pc
	And  	Proc.Nr_Sequencia  = part.nr_seq_conta_proc
	and  	coalesce(nr_seq_proc_pc::text, '') = ''
	and	proc.ie_status 	in ('A', 'F', 'L', 'P', 'U')
	
union all

	select  part.ie_status,
		null qt_apresentada,
		part.qt_liberada,
		null vl_apresentado,
		part.vl_calculado,
		part.vl_participante,
		part.vl_glosa,
		Part.Ie_Glosa,
		part.nr_sequencia
	From  	Pls_Conta_Proc_V Proc,
		pls_proc_participante  part
        Where   Proc.Nr_Seq_Protocolo    = Nr_Seq_Protocolo_Pc
        And  	Proc.Nr_Sequencia  	= part.nr_seq_conta_proc
        and  	coalesce(nr_seq_conta_pc::text, '') = ''
	and	proc.ie_status 	in ('A', 'F', 'L', 'P', 'U')

union all

        select  part.ie_status,
		null qt_apresentada,
		part.qt_liberada,
		null vl_apresentado,
		part.vl_calculado,
		part.vl_participante,
		part.vl_glosa,
		Part.Ie_Glosa,
		part.nr_sequencia
        From    Pls_Conta_Proc_V Proc,
		pls_proc_participante  part
        Where  Proc.Nr_Seq_Lote_Conta   = Nr_Seq_Lote_Pc
        And  Proc.Nr_Sequencia  	= part.nr_seq_conta_proc
        And  coalesce(Nr_Seq_Conta_Pc::text, '') = ''
        and  coalesce(nr_seq_protocolo_pc::text, '') = ''  
	and  proc.ie_status 	in ('A', 'F', 'L', 'P', 'U')
	
union all

	select  part.ie_status,
		null qt_apresentada,
		part.qt_liberada,
		null vl_apresentado,
		part.vl_calculado,
		part.vl_participante,
		part.vl_glosa,
		part.ie_glosa,
		part.nr_sequencia
	From  	Pls_Conta_Proc Proc,
		pls_proc_participante  part
	Where  	1 = 1 
	And  	coalesce(Nr_Seq_Conta_Pc::text, '') = ''
        and  	coalesce(nr_seq_protocolo_pc::text, '') = ''  
	and	coalesce(Nr_Seq_Lote_Pc::text, '') = ''
	and   	exists ( select lote.nr_seq_conta
			 from Pls_Cta_Lote_Proc_Conta Lote
			 where Lote.Nr_Seq_Lote_Processo = Nr_Seq_Lote_Processo_Pc
			 and   lote.nr_seq_conta = proc.nr_seq_conta)
	and  proc.nr_sequencia = part.nr_seq_conta_proc			
	and  proc.ie_status in ('A', 'F', 'L', 'P', 'U');
BEGIN

nr_sequencia_w := 0;
tb_seq_item_w.delete;		
tb_status_pagamento_w.delete;
nr_indice_w	:= 0;

for	r_C01_w in C01(nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,	
                       nr_seq_conta_p, nr_seq_conta_proc_p, nm_usuario_p, cd_estabelecimento_p) loop	
		
	ie_status_pagamento_w	:= pls_analise_obter_status_pag(	r_C01_w.ie_status,
									r_C01_w.qt_apresentada,
									r_C01_w.qt_liberada,
									r_C01_w.vl_apresentado,
									r_C01_w.vl_calculado,
									r_C01_w.vl_participante,
									r_C01_w.vl_glosa,
									r_C01_w.ie_glosa);

	ie_ocor_pagamento_w	:= pls_gerencia_dados_ocor_pck.pls_obter_se_glosa_ocor(nr_seq_conta_p);
		
	if (ie_ocor_pagamento_w = 'N') then
			select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
			into STRICT 	ie_ocor_pagamento_w
			from	pls_ocorrencia_benef	b,
				pls_ocorrencia		c
			where	b.nr_seq_conta_proc	= r_C01_w.nr_sequencia
			and	c.nr_sequencia		= b.nr_seq_ocorrencia
			and	b.ie_situacao		= 'A'
			and	c.ie_situacao		= 'A'
			and	c.ie_glosar_pagamento	= 'S'
			and ( SELECT	count(1)
				  From		Tiss_Motivo_Glosa A
				  Where		A.Cd_Motivo_Tiss = '1705'
				  And		A.Nr_Sequencia = C.Nr_Seq_Motivo_Glosa) = 0
				
			and ( select	count(1)
				  from 		pls_conta_glosa x
				  where		x.nr_seq_ocorrencia_benef	= b.nr_sequencia
				  And		X.Ie_Lib_Manual			= 'S') = 0
					 
			and ( select	count(1)
					 from 	pls_conta_glosa x
					 Where	X.Nr_Sequencia	= B.Nr_Seq_Glosa
					 and	x.ie_lib_manual	= 'S') = 0;
	end if;
									
	if (ie_ocor_pagamento_w = 'S') 	and (coalesce(r_C01_w.ie_glosa,'N')	<> 'S')	then
		ie_status_pagamento_w	:= 'I';
	end if;
	
	tb_seq_item_w(nr_indice_w) :=	r_C01_w.nr_sequencia;		
	tb_status_pagamento_w(nr_indice_w) := ie_status_pagamento_w;
	
	if (nr_indice_w  = pls_util_cta_pck.qt_registro_transacao_w) then
		
		CALL pls_cta_valorizacao_pck.atualiza_status_item( tb_seq_item_w, tb_status_pagamento_w, 'R');	
		tb_seq_item_w.delete;
		tb_status_pagamento_w.delete;
		nr_indice_w	:= 0;
		
	else
		nr_indice_w := nr_indice_w + 1;
	end if;
								
end loop;
--Faz chamada para atualizacao de itens pois podem sobrar alguns nas listas.

CALL pls_cta_valorizacao_pck.atualiza_status_item( tb_seq_item_w, tb_status_pagamento_w, 'R');
			
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cta_valorizacao_pck.pls_gerencia_status_pagto_part ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
