SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_cancelar_comissao ( nr_seq_comissao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


	c_comissao CURSOR FOR
	SELECT	h.nm_tabela,
		h.nm_atributo,
		75 nr_seq_info_ctb,
		d.vl_comissao vl_contabil,
		a.dt_referencia dt_movimento,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		h.cd_tipo_lote_contabil,
		h.nr_seq_info,
		count(case h.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	FROM ctb_documento h, pls_vendedor g, pls_segurado e, pls_comissao_beneficiario c, pls_comissao b, pls_lote_comissao a, pls_comissao_benef_item d
LEFT OUTER JOIN pls_plano f ON (d.nr_seq_plano = f.nr_sequencia)
WHERE a.nr_sequencia	= b.nr_seq_lote and g.nr_sequencia	= b.nr_seq_canal_venda and b.nr_sequencia	= c.nr_seq_comissao and c.nr_sequencia	= d.nr_seq_comissao_benef and e.nr_sequencia	= c.nr_seq_segurado  and h.nr_documento 	= a.nr_sequencia and h.nr_seq_doc_compl = b.nr_sequencia and h.nr_doc_analitico = d.nr_sequencia and h.nm_tabela	= 'PLS_COMISSAO_BENEF_ITEM' and h.nm_atributo 	= 'VL_COMISSAO' and h.nr_seq_info 	= 75 and coalesce(h.ds_origem, 'X') 	<> 'ESTORNO' and b.nr_sequencia  = nr_seq_comissao_p group by h.nm_tabela,
		h.nm_atributo,
		d.vl_comissao,
		a.dt_referencia,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		h.cd_tipo_lote_contabil,
		h.nr_seq_info
	
union all

	SELECT	e.nm_tabela,
		e.nm_atributo,
		75 nr_seq_info_ctb,
		c.vl_lancamento vl_contabil,
		a.dt_referencia dt_movimento,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		e.cd_tipo_lote_contabil,
		e.nr_seq_info,
		count(case e.ie_situacao_ctb when 'P' then 1 end) qt_pendente	
	from	pls_lote_comissao		a,
		pls_comissao			b,
		pls_repasse_lanc		c,
		pls_vendedor			d,
		ctb_documento			e
	where	a.nr_sequencia	= b.nr_seq_lote
	and	d.nr_sequencia	= b.nr_seq_canal_venda
	and	b.nr_sequencia	= c.nr_seq_comissao
	and	e.nr_documento	= a.nr_sequencia
	and	e.nr_seq_doc_compl = b.nr_sequencia
	and	e.nr_doc_analitico = d.nr_sequencia
	and	e.nm_tabela 	= 'PLS_REPASSE_LANC'
	and	e.nm_atributo	= 'VL_LANCAMENTO'
	and	e.nr_seq_info 	= 75
	and	coalesce(e.ds_origem, 'X') 	<> 'ESTORNO'
	and	b.nr_sequencia	= nr_seq_comissao_p
	group by e.nm_tabela,
		e.nm_atributo,
		c.vl_lancamento,
		a.dt_referencia,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		e.cd_tipo_lote_contabil,
		e.nr_seq_info;

	vet_comissao c_comissao%rowtype;
	
BEGIN

	open c_comissao;
	loop
	fetch c_comissao into
		vet_comissao;
	EXIT WHEN NOT FOUND; /* apply on c_comissao */
		begin

		if (vet_comissao.qt_pendente > 0) then
			delete 	FROM ctb_documento
			where	nr_documento			= vet_comissao.nr_documento
			and	nr_seq_doc_compl 		= vet_comissao.nr_seq_doc_compl
			and	nr_doc_analitico 		= vet_comissao.nr_doc_analitico
			and 	nm_tabela 			= vet_comissao.nm_tabela
			and	nm_atributo 			= vet_comissao.nm_atributo
			and	cd_tipo_lote_contabil 		= vet_comissao.cd_tipo_lote_contabil
			and	nr_seq_info 			= vet_comissao.nr_seq_info
			and	ie_situacao_ctb 		= 'P'
			and	coalesce(ds_origem, 'X')		<> 'ESTORNO';
		else
			CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
									pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
									24,
									null,
									vet_comissao.nr_seq_info_ctb,
									vet_comissao.nr_documento,
									vet_comissao.nr_seq_doc_compl,
									vet_comissao.nr_doc_analitico,
									vet_comissao.vl_contabil * -1, --Inverte o valor pois e estorno
									vet_comissao.nm_tabela,
									vet_comissao.nm_atributo,
									nm_usuario_p,
									'P',
									'ESTORNO');
		end if;

		end;
	end loop;
	close c_comissao;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_cancelar_comissao ( nr_seq_comissao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;
