CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_desf_fech_disc ( nr_seq_lote_discussao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

	
	dt_fechamento_w		timestamp;
	dt_ref_inicial_w	timestamp;
	dt_ref_final_w		timestamp;
	
	c_discussao CURSOR FOR
	SELECT	distinct d.vl_aceito vl_contabil,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_PROC'
	and	g.nm_atributo		= 'VL_ACEITO'
	and	coalesce(g.ds_origem, 'X')	not in ('ESTORNO', 'CANCELAMENTO')
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and (a.ie_status		= 'F'
	and	exists (SELECT	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_aceito,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info
	
union all

	select	distinct d.vl_negado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_PROC'
	and	g.nm_atributo		= 'VL_NEGADO'
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	coalesce(d.ie_tipo_acordo,'X') <> '12'
	and	coalesce(g.ds_origem, 'X')	not in ('ESTORNO', 'CANCELAMENTO')
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_negado,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info
	
union all

	select	distinct d.vl_contestado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_PROC'
	and	g.nm_atributo		= 'VL_CONTESTADO'
	and	coalesce(g.ds_origem, 'X') 	not in ('ESTORNO', 'CANCELAMENTO')
	and	coalesce(a.ie_tipo_arquivo,'0') = '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_contestado,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info
	
union all

	select	distinct d.vl_aceito,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_MAT'
	and	g.nm_atributo		= 'VL_ACEITO'
	and	coalesce(g.ds_origem, 'X') 	not in ('ESTORNO', 'CANCELAMENTO')
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_aceito,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info
	
union all

	select	distinct d.vl_negado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_MAT'
	and	g.nm_atributo		= 'VL_NEGADO'
	and	coalesce(g.ds_origem, 'X') 	not in ('ESTORNO', 'CANCELAMENTO')
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	coalesce(d.ie_tipo_acordo,'X') <> '12'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_negado,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info
	
union all

	select	distinct d.vl_contestado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info,
		count(case g.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	ctb_documento			g,
		pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	g.nr_documento		= a.nr_sequencia
	and	g.nr_seq_doc_compl 	= b.nr_sequencia
	and	g.nr_doc_analitico	= d.nr_sequencia
	and	g.nm_tabela		= 'PLS_DISCUSSAO_MAT'
	and	g.nm_atributo		= 'VL_CONTESTADO'
	and	coalesce(g.ds_origem, 'X') 	not in ('ESTORNO', 'CANCELAMENTO')
	and	coalesce(a.ie_tipo_arquivo,'0') = '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	group by d.vl_contestado,
		a.dt_fechamento,
		g.nm_tabela,
		g.nm_atributo,
		a.nr_sequencia,
		b.nr_sequencia,
		d.nr_sequencia,
		g.cd_tipo_lote_contabil,
		g.nr_seq_info;

	vet_discussao c_discussao%rowtype;

	
BEGIN

	select	dt_fechamento
	into STRICT	dt_fechamento_w
	from	pls_lote_discussao
	where	nr_sequencia = nr_seq_lote_discussao_p;

	dt_ref_inicial_w	:= trunc(dt_fechamento_w, 'month');
	dt_ref_final_w		:= fim_dia(fim_mes(dt_ref_inicial_w));

	open c_discussao;
	loop
	fetch c_discussao into
		vet_discussao;
	EXIT WHEN NOT FOUND; /* apply on c_discussao */
		begin
		if (vet_discussao.qt_pendente > 0) then
			delete 	FROM ctb_documento
			where	nr_documento			= vet_discussao.nr_documento
			and	nr_seq_doc_compl 		= vet_discussao.nr_seq_doc_compl
			and	nr_doc_analitico 		= vet_discussao.nr_doc_analitico
			and 	nm_tabela 			= vet_discussao.nm_tabela
			and	nm_atributo 			= vet_discussao.nm_atributo
			and	cd_tipo_lote_contabil 		= vet_discussao.cd_tipo_lote_contabil
			and	nr_seq_info 			= vet_discussao.nr_seq_info
			and	ie_situacao_ctb 		= 'P'
			and	coalesce(ds_origem, 'X')		<> 'ESTORNO';
		else
			if (coalesce(vet_discussao.vl_contabil, 0) <> 0) then

				CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
										pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
										38,
										null,
										vet_discussao.nr_seq_info_ctb,
										vet_discussao.nr_documento,
										vet_discussao.nr_seq_doc_compl,
										vet_discussao.nr_doc_analitico,
										vet_discussao.vl_contabil * -1,  --Inverte o valor pois e estorno
										vet_discussao.nm_tabela,
										vet_discussao.nm_atributo,
										nm_usuario_p,
										'P',
										'ESTORNO');

			end if;
		end if;
		end;
	end loop;
	close c_discussao;

END;

