SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_desf_ret_trib ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


	ctb_doc_w 		ctb_documento%rowtype;

	c_movimentos CURSOR FOR
	SELECT	coalesce(a.vl_movimento,0) * (-1) vl_contabil,
		c.nm_tabela,
		c.nm_atributo,
		c.nr_documento,
		c.nr_seq_doc_compl,
		c.nr_doc_analitico,
		c.cd_tipo_lote_contabil,
		c.nr_seq_info,
		count(case c.ie_situacao_ctb when 'P' then 1 end) qt_pendente
	from	pls_evento_movimento	a,
		pls_lote_evento		b,
		ctb_documento		c
	where	b.nr_sequencia			= a.nr_seq_lote
	and	coalesce(b.nr_seq_lote_ret_trib,0)	<> 0
	and	b.nr_sequencia 			= nr_seq_lote_p
	and	c.cd_tipo_lote_contabil 	= 41
	and	c.nr_seq_info			= 39
	and	c.nm_tabela			= 'PLS_EVENTO_MOVIMENTO'
	and	c.nm_atributo			= 'VL_MOVIMENTO'
	and	coalesce(c.ds_origem, 'X') 		<> 'ESTORNO'
	and	c.nr_documento			= b.nr_seq_lote_ret_trib
	and	c.nr_seq_doc_compl		= b.nr_sequencia
	and	c.nr_doc_analitico		= a.nr_sequencia
	group by a.vl_movimento,
		c.nm_tabela,
		c.nm_atributo,
		c.nr_documento,
		c.nr_seq_doc_compl,
		c.nr_doc_analitico,
		c.cd_tipo_lote_contabil,
		c.nr_seq_info;

	vet_movimento c_movimentos%rowtype;

	
BEGIN
	open c_movimentos;
	loop
	fetch c_movimentos into
		vet_movimento;
	EXIT WHEN NOT FOUND; /* apply on c_movimentos */
		begin

		if (vet_movimento.qt_pendente > 0) then
			delete 	FROM ctb_documento
			where	nr_documento 			= vet_movimento.nr_documento
			and	nr_seq_doc_compl 		= vet_movimento.nr_seq_doc_compl
			and	nr_doc_analitico 		= vet_movimento.nr_doc_analitico
			and 	nm_tabela 			= vet_movimento.nm_tabela
			and	nm_atributo 			= vet_movimento.nm_atributo
			and	cd_tipo_lote_contabil 		= vet_movimento.cd_tipo_lote_contabil
			and	nr_seq_info 			= vet_movimento.nr_seq_info
			and	ie_situacao_ctb 		= 'P'
			and	coalesce(ds_origem, 'X')		<> 'ESTORNO';
		else
			CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
									pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
									41,
									null,		
									39,
									vet_movimento.nr_documento,
									vet_movimento.nr_seq_doc_compl,
									vet_movimento.nr_doc_analitico,
									vet_movimento.vl_contabil * -1, -- Inverte o valor pois e um estorno
									vet_movimento.nm_tabela,
									vet_movimento.nm_atributo,
									nm_usuario_p);

			select 	*
			into STRICT	ctb_doc_w
			from (SELECT *
				from   	ctb_documento a
				where 	a.nr_documento 		= vet_movimento.nr_documento
				and	a.nr_seq_doc_compl	= vet_movimento.nr_seq_doc_compl
				and	a.nr_doc_analitico	= vet_movimento.nr_doc_analitico
				order by nr_sequencia desc) alias0 LIMIT 1;

			CALL pls_contab_onl_lote_fin_pck.contabiliza_documento(ctb_doc_w, nm_usuario_p);
		
		end if;
		end;
	end loop;
	close c_movimentos;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
