CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_fechar_disc ( nr_seq_lote_discussao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

	
	dt_fechamento_w		timestamp;
	dt_ref_inicial_w	timestamp;
	dt_ref_final_w		timestamp;
	qt_movimento_w		bigint := 0;
	nr_seq_atualizacao_w	pls_atualizacao_contabil.nr_sequencia%type := 0;
	
	c_discussao CURSOR FOR
	SELECT	d.vl_aceito vl_contabil,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_PROC' nm_tabela,
		'VL_ACEITO' nm_atributo,
		d.nr_sequencia nr_seq_disc_proc,
		null nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (SELECT	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	
union all

	select	d.vl_negado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_PROC' nm_tabela,
		'VL_NEGADO' nm_atributo,
		d.nr_sequencia nr_seq_disc_proc,
		null nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	coalesce(d.ie_tipo_acordo,'X') <> '12'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	
union all

	select	d.vl_contestado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_PROC' nm_tabela,
		'VL_CONTESTADO' nm_atributo,
		d.nr_sequencia nr_seq_disc_proc,
		null nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_proc			f,
		pls_conta			e,
		pls_discussao_proc		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_proc
	and	coalesce(a.ie_tipo_arquivo,'0') = '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	
union all

	select	d.vl_aceito,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_MAT' nm_tabela,
		'VL_ACEITO' nm_atributo,
		null nr_seq_disc_proc,
		d.nr_sequencia nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	
union all

	select	d.vl_negado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_MAT' nm_tabela,
		'VL_NEGADO' nm_atributo,
		null nr_seq_disc_proc,
		d.nr_sequencia nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	coalesce(a.ie_tipo_arquivo,'0') <> '1'
	and	coalesce(d.ie_tipo_acordo,'X') <> '12'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p))
	
union all

	select	d.vl_contestado,
		trunc(a.dt_fechamento,'dd') dt_movimento,
		29 nr_seq_info_ctb,
		'PLS_DISCUSSAO_MAT' nm_tabela,
		'VL_CONTESTADO' nm_atributo,
		null nr_seq_disc_proc,
		d.nr_sequencia nr_seq_disc_mat,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	from	pls_conta_mat			f,
		pls_conta			e,
		pls_discussao_mat		d,
		pls_contestacao			c,
		pls_contestacao_discussao	b,
		pls_lote_discussao		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	c.nr_sequencia		= b.nr_seq_contestacao
	and	e.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= d.nr_seq_discussao
	and	f.nr_sequencia		= d.nr_seq_conta_mat
	and	coalesce(a.ie_tipo_arquivo,'0') = '1'
	and	a.nr_sequencia		= nr_seq_lote_discussao_p
	and (a.ie_status		= 'F'
	and	exists (select	1
			from	pls_lote_contestacao x
			where	x.nr_sequencia		= a.nr_seq_lote_contest
			and	x.cd_estabelecimento	= cd_estabelecimento_p));
	vet_discussao c_discussao%rowtype;

	
BEGIN

	select	dt_fechamento
	into STRICT	dt_fechamento_w
	from	pls_lote_discussao
	where	nr_sequencia = nr_seq_lote_discussao_p;

	dt_ref_inicial_w	:= trunc(dt_fechamento_w, 'month');
	dt_ref_final_w		:= fim_dia(fim_mes(dt_ref_inicial_w));

	pls_gerar_atualizacao_contabil(	dt_ref_inicial_w,
					nm_usuario_p,
					cd_estabelecimento_p,
					38,
					'G',
					qt_movimento_w,
					nr_seq_atualizacao_w);

	open c_discussao;
	loop
	fetch c_discussao into
		vet_discussao;
	EXIT WHEN NOT FOUND; /* apply on c_discussao */
		begin

		if (coalesce(vet_discussao.vl_contabil, 0) <> 0) then
		
			ctb_pls_atualizar_discussao(			nr_seq_lote_discussao_p,
									nr_seq_atualizacao_w,
									nm_usuario_p,
									cd_estabelecimento_p,
									qt_movimento_w,
									vet_discussao.nr_seq_disc_proc,
									vet_discussao.nr_seq_disc_mat);

			CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
									pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
									38,
									null,
									vet_discussao.nr_seq_info_ctb,
									vet_discussao.nr_documento,
									vet_discussao.nr_seq_doc_compl,
									vet_discussao.nr_doc_analitico,
									vet_discussao.vl_contabil,
									vet_discussao.nm_tabela,
									vet_discussao.nm_atributo,
									nm_usuario_p,
									'P');

		end if;

		end;
	end loop;
	close c_discussao;

	pls_gerar_atualizacao_contabil(	dt_ref_inicial_w,
					nm_usuario_p,
					cd_estabelecimento_p,
					38,
					'A',
					qt_movimento_w,
					nr_seq_atualizacao_w);
END;

$body$
