CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_gerar_ret_trib ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


	nr_seq_atualizacao_w		pls_atualizacao_contabil.nr_sequencia%type := 0;
	qt_movimento_w			bigint := 0;
	dt_referencia_w			timestamp;

	c_movimentos CURSOR FOR
	SELECT	coalesce(a.vl_movimento,0) * (-1) vl_contabil,
		trunc(b.dt_competencia) dt_competencia,
		'PLS_EVENTO_MOVIMENTO' nm_tabela,
		'VL_MOVIMENTO' nm_atributo,
		b.nr_seq_lote_ret_trib nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		a.nr_sequencia nr_doc_analitico,
		a.nr_sequencia nr_seq_evento_movimento,
		b.nr_sequencia nr_seq_lote_evento
	from	pls_evento_movimento	a,
		pls_lote_evento		b
	where	b.nr_sequencia		= a.nr_seq_lote
	and	coalesce(b.nr_seq_lote_ret_trib,0)	<> 0
	and	b.nr_sequencia 		= nr_seq_lote_p;

	vet_movimento c_movimentos%rowtype;

	
BEGIN

	select	trunc(a.dt_competencia,'month')
	into STRICT	dt_referencia_w
	from	pls_lote_evento	a
	where	a.nr_sequencia	= nr_seq_lote_p;

	open c_movimentos;
	loop
	fetch c_movimentos into
		vet_movimento;
	EXIT WHEN NOT FOUND; /* apply on c_movimentos */
		begin


		pls_gerar_atualizacao_contabil(	dt_referencia_w,
						nm_usuario_p,
						cd_estabelecimento_p,
						41,
						'G',
						qt_movimento_w,
						nr_seq_atualizacao_w);
		
		ctb_pls_atualizar_prod_eve_in(	vet_movimento.nr_seq_lote_evento,
						vet_movimento.nr_seq_evento_movimento,
						nr_seq_atualizacao_w,
						nm_usuario_p,
						cd_estabelecimento_p,
						qt_movimento_w);

		pls_gerar_atualizacao_contabil(	dt_referencia_w,
						nm_usuario_p,
						cd_estabelecimento_p,
						41,
						'A',
						qt_movimento_w,
						nr_seq_atualizacao_w);

		CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
								pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
								41,
								null,		
								39,
								vet_movimento.nr_documento,
								vet_movimento.nr_seq_doc_compl,
								vet_movimento.nr_doc_analitico,
								vet_movimento.vl_contabil,
								vet_movimento.nm_tabela,
								vet_movimento.nm_atributo,
								nm_usuario_p);
		end;
	end loop;
	close c_movimentos;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
