CREATE OR REPLACE PROCEDURE pls_ctb_onl_gravar_movto_pck.gravar_movto_pagar_comissao ( nr_seq_lote_comissao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


	c_comissao CURSOR FOR
	SELECT	'PLS_COMISSAO_BENEF_ITEM' nm_tabela,
		'VL_COMISSAO' nm_atributo,
		75 nr_seq_info_ctb,
		d.vl_comissao vl_contabil,
		a.dt_referencia dt_movimento,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico
	FROM pls_vendedor g, pls_segurado e, pls_comissao_beneficiario c, pls_comissao b, pls_lote_comissao a, pls_comissao_benef_item d
LEFT OUTER JOIN pls_plano f ON (d.nr_seq_plano = f.nr_sequencia)
WHERE a.nr_sequencia	= b.nr_seq_lote and g.nr_sequencia	= b.nr_seq_canal_venda and b.nr_sequencia	= c.nr_seq_comissao and c.nr_sequencia	= d.nr_seq_comissao_benef and e.nr_sequencia	= c.nr_seq_segurado  and a.nr_sequencia  = nr_seq_lote_comissao_p
	
union all

	SELECT	'PLS_REPASSE_LANC' nm_tabela,
		'VL_LANCAMENTO' nm_atributo,
		75 nr_seq_info_ctb,
		c.vl_lancamento vl_contabil,
		a.dt_referencia dt_movimento,
		a.nr_sequencia nr_documento,
		b.nr_sequencia nr_seq_doc_compl,
		d.nr_sequencia nr_doc_analitico		
	from	pls_lote_comissao		a,
		pls_comissao			b,
		pls_repasse_lanc		c,
		pls_vendedor			d
	where	a.nr_sequencia	= b.nr_seq_lote
	and	d.nr_sequencia	= b.nr_seq_canal_venda
	and	b.nr_sequencia	= c.nr_seq_comissao
	and	a.nr_sequencia	= nr_seq_lote_comissao_p;

	vet_comissao c_comissao%rowtype;
	
BEGIN

	CALL cbt_pls_atualizar_comissao(nr_seq_lote_comissao_p, cd_estabelecimento_p, nm_usuario_p);

	open c_comissao;
	loop
	fetch c_comissao into
		vet_comissao;
	EXIT WHEN NOT FOUND; /* apply on c_comissao */
		begin
		
		CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_p,
								pkg_date_utils.start_of(clock_timestamp(), 'DAY'),
								24,
								null,
								vet_comissao.nr_seq_info_ctb,
								vet_comissao.nr_documento,
								vet_comissao.nr_seq_doc_compl,
								vet_comissao.nr_doc_analitico,
								vet_comissao.vl_contabil,
								vet_comissao.nm_tabela,
								vet_comissao.nm_atributo,
								nm_usuario_p);

		end;
	end loop;
	close c_comissao;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
