CREATE OR REPLACE PROCEDURE pls_dados_atuariais_pck.desfazer_dados_lote ( nr_seq_lote_p pls_atuarial_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Desfaz (Exclui) os dados do lote, conforme o tipo do arquivo (tipo do lote)

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


lote_atuarial_w	lote_atuarial;


BEGIN

lote_atuarial_w := pls_dados_atuariais_pck.retorna_lote_atuarial(nr_seq_lote_p);

-- Limpar log de 60 dias atras

CALL pls_dados_atuariais_pck.limpar_log();

-- Beneficiarios

if (lote_atuarial_w.ie_tipo_arquivo = '1') then

	delete	FROM pls_atu_benef_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Contas medicas

if (lote_atuarial_w.ie_tipo_arquivo = '2') then

	delete	FROM pls_atu_conta_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Estipulantes

if (lote_atuarial_w.ie_tipo_arquivo = '3') then

	delete	FROM pls_atu_estip_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Mensalidades

if (lote_atuarial_w.ie_tipo_arquivo = '4') then

	delete	FROM pls_atu_mens_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Prestadores

if (lote_atuarial_w.ie_tipo_arquivo = '5') then

	delete	FROM pls_atu_prest_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Produtos

if (lote_atuarial_w.ie_tipo_arquivo = '6') then

	delete	FROM pls_atu_plano_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

-- Procedimentos

if (lote_atuarial_w.ie_tipo_arquivo = '7') then

	delete	FROM pls_atu_proced_item
	where	nr_seq_lote	= lote_atuarial_w.nr_sequencia;
end if;

lote_atuarial_w := pls_dados_atuariais_pck.lote_em_aberto(lote_atuarial_w, nm_usuario_p);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
