CREATE OR REPLACE PROCEDURE pls_dados_atuariais_pck.gerar_dados_atuariais (nr_seq_lote_p pls_atuarial_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Rotina que vai fazer todas as tratativas para chamar uma JOB para executar o processo.

	Essa rotina apenas carregar as informacoes do lote e vai preparar a JOB e marcar o lote 
	como aguardando processamento, nenhuma outra regra de negocio deve ser colocada aqui.

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

lote_atuarial_w	lote_atuarial;


BEGIN

-- "Limpa" as jobs ja utilizadas para gerar os dados

CALL pls_dados_atuariais_pck.limpa_job_gerar_lote();

lote_atuarial_w := pls_dados_atuariais_pck.retorna_lote_atuarial(nr_seq_lote_p);

-- Se esta "Em Aberto"

if (lote_atuarial_w.ie_status = '1') then

	-- Etapa "Aguardando processamento"

	lote_atuarial_w := pls_dados_atuariais_pck.aguardando_process_lote(lote_atuarial_w, nm_usuario_p);
	
	-- cria a JOB

	CALL pls_dados_atuariais_pck.criar_job_gerar_lote(lote_atuarial_w.nr_sequencia, nm_usuario_p);

end if;

END;

$body$
