CREATE OR REPLACE FUNCTION pls_dados_atuariais_pck.obter_bind_log (dado_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE


qt_reg_w	integer := 0;
ds_retorno_w	varchar(32000) := null;


BEGIN
qt_reg_w := (dado_bind_p.count);

while(qt_reg_w > 0) loop
	ds_retorno_w := ds_retorno_w ||' '|| dado_bind_p[qt_reg_w].ds_nome;
	
