		ds_retorno_w := ds_retorno_w ||'= '|| dado_bind_p[qt_reg_w].ds_valor;
		
	elsif (dado_bind_p[qt_reg_w](.dt_valor IS NOT NULL AND .dt_valor::text <> '')) then
		ds_retorno_w := ds_retorno_w ||'= '|| dado_bind_p[qt_reg_w].dt_valor;
	
	elsif (dado_bind_p[qt_reg_w](.vl_valor IS NOT NULL AND .vl_valor::text <> '')) then
		ds_retorno_w := ds_retorno_w ||'= '|| dado_bind_p[qt_reg_w].vl_valor;
	end if;
	
	qt_reg_w := qt_reg_w - 1;
end loop;

return;

END;

