CREATE OR REPLACE FUNCTION pls_dados_atuariais_pck.retorna_regra_arquivo ( nr_seq_regra_p pls_atuarial_arq_regra.nr_sequencia%type) RETURNS REGRA_ARQUIVO AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna os dados pertinentes a regra do arquivo

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

regra_retorno_w	regra_arquivo;

BEGIN

select	max(a.nr_sequencia),
	max(a.ds_arquivo),
	max(a.ie_tipo_arquivo),
	max(a.ie_situacao),
	max(a.cd_estabelecimento)
into STRICT	regra_retorno_w.nr_seq_regra,
	regra_retorno_w.ds_arquivo,
	regra_retorno_w.ie_tipo_arquivo,
	regra_retorno_w.ie_situacao,
	regra_retorno_w.cd_estabelecimento
from	pls_atuarial_arq_regra	a
where	a.nr_sequencia		= nr_seq_regra_p;

return regra_retorno_w;

END;

$body$
