-- atualizar_benef_ir
CREATE OR REPLACE PROCEDURE pls_declaracao_ir_pck.atualizar_benef_ir ( nr_seq_lote_p pls_lote_mens_ir.nr_sequencia%type, nr_seq_pagador_ir_p pls_mens_pagador_ir.nr_sequencia%type) AS $body$
DECLARE


_ora2pg_r RECORD;
nr_indice_w			integer;
tb_nr_seq_benef_ir_w		pls_util_cta_pck.t_number_table;
tb_vl_mensalidade_w		pls_util_cta_pck.t_number_table;
tb_vl_coparticipacao_w		pls_util_cta_pck.t_number_table;
tb_vl_reembolso_w		pls_util_cta_pck.t_number_table;
tb_vl_desconto_w		pls_util_cta_pck.t_number_table;
tb_vl_devolucao_w		pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_lote_pc pls_lote_mens_ir.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia nr_seq_benef_ir
	from	pls_mens_beneficiario_ir b,
		pls_mens_pagador_ir c
	where	c.nr_sequencia	= b.nr_seq_pagador_ir
	and	c.nr_seq_lote	= nr_seq_lote_pc
	and	((c.nr_sequencia = nr_seq_pagador_ir_p) or (coalesce(nr_seq_pagador_ir_p::text, '') = ''))
	and	exists (SELECT	1
			from	pls_mens_detalhe_ir a
			where	b.nr_sequencia	= a.nr_seq_beneficiario_ir);

BEGIN

nr_indice_w	:= 0;
tb_nr_seq_benef_ir_w.delete;
SELECT * FROM pls_declaracao_ir_pck.update_benef_ir(tb_nr_seq_benef_ir_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_benef_ir_w := _ora2pg_r.tb_nr_seq_benef_ir_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p;

for r_c01_w in C01(nr_seq_lote_p) loop
	begin
	tb_nr_seq_benef_ir_w(nr_indice_w)	:= r_c01_w.nr_seq_benef_ir;
	
	select	sum(vl_mensalidade),
		sum(vl_coparticipacao),
		sum(vl_reembolso),
		sum(vl_desconto),
		sum(vl_devolucao)
	into STRICT	tb_vl_mensalidade_w(nr_indice_w),
		tb_vl_coparticipacao_w(nr_indice_w),
		tb_vl_reembolso_w(nr_indice_w),
		tb_vl_desconto_w(nr_indice_w),
		tb_vl_devolucao_w(nr_indice_w)
	from	pls_mens_detalhe_ir
	where	nr_seq_beneficiario_ir = r_c01_w.nr_seq_benef_ir;

	if (nr_indice_w >= pls_util_pck.qt_registro_transacao_w) then
		SELECT * FROM pls_declaracao_ir_pck.update_benef_ir(tb_nr_seq_benef_ir_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_benef_ir_w := _ora2pg_r.tb_nr_seq_benef_ir_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p;
		
		nr_indice_w	:= 0;
	else
		nr_indice_w	:= nr_indice_w + 1;
	end if;
	end;
end loop;

SELECT * FROM pls_declaracao_ir_pck.update_benef_ir(tb_nr_seq_benef_ir_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_benef_ir_w := _ora2pg_r.tb_nr_seq_benef_ir_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p;

END;

$body$
